mapper.update:

UPDATE VISITOR_BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}


UPDATE VISITOR_BOARD
SET CONTENT = #{content}
WHERE BOARD_NO = #{boardNo}


UPDATE VISITOR_COMMENT
SET CONTENT = #{content}, ENROLL_DATE = SYSDATE
WHERE COMMENT_NO = #{commentNo}


UPDATE VISITOR_COMMENT
SET STATUS = 'N'
WHERE COMMENT_NO = #{commentNo}


UPDATE NEIGHBOR
SET STATUS = 'N'
WHERE NEIGHBOR_NO = #{neighborNo}


UPDATE DAILYLOG
SET CONTENT = #{content},
	FILE_ORIGIN_NAME = #{fileName},
	FILE_SERVER_NAME = #{serverName}
WHERE LOG_NO = #{logNo}


UPDATE DAILYLOG
SET STATUS = 'N'
WHERE LOG_NO = #{logNo}


UPDATE DAILYLOG_COMMENT
SET STATUS = 'N'
WHERE LOG_NO = #{logNo}

mapper.delete:

UPDATE DAILYLOG_COMMENT
SET STATUS = 'N'
WHERE COMMENT_NO = #{commentNo}
OR COMMENT_GROUP = #{commentNo}

mapper.update:

UPDATE DAILYLOG_COMMENT
SET CONTENT = #{content}
WHERE COMMENT_NO = #{commentNo}
