mapper.update:
UPDATE INQUIRY
SET REPLYCOUNT = (
					SELECT COUNT(*)
					FROM ANSWER
					WHERE REPER_NO=#{ino}
)
WHERE INQUIRY_NO=#{ino}

UPDATE ANSWER
SET ANSWER = #{answer}
WHERE ANSWER_NO = #{answerNo}

UPDATE INQUIRY
SET TITLE = #{title}, CONTENT = #{content}, CATEGORY = #{category}, OPEN = #{open}
WHERE INQUIRY_NO = #{inquiryNo}

mapper.delete:
UPDATE INQUIRY
SET STATUS = 'N'
WHERE INQUIRY_NO = #{ino}

UPDATE FAQ
SET STATUS = 'N'
WHERE FAQ_NO = #{fno}

mapper.update:
UPDATE FAQ
SET QUESTION = #{question}, ANSWER = #{answer}, CATEGORY = #{category}, STATUS = #{status}
WHERE FAQ_NO = #{faqNo}

UPDATE NOTICE
SET CATEGORY = #{category}, TITLE = #{title}, CONTENT = #{content}, STATUS = #{status}
WHERE NOTICE_NO = #{noticeNo}

mapper.delete:
UPDATE NOTICE
SET STATUS = 'N'
WHERE NOTICE_NO = #{nno}
