mapper.update:
UPDATE BOARD
SET B_STATUS = 'N' 
WHERE B_NO=#{bno}

UPDATE BOARD
SET B_CATEGORY_NO=#{BCategoryNo}, B_TITLE=#{BTitle}, B_CONTENT=#{BContent}, B_ORIGIN_NAME=#{BOriginName}, B_CHANGE_NAME=#{BChangeName}
WHERE B_NO=#{bNo}

UPDATE BOARD
SET ADB_CATEGORY_NO=#{bReport}, B_STATUS = 'R'
WHERE B_NO=#{bno}
